compose-order-query.inputs:
let data = dynamic(
@{string(outputs('blob-chunk'))}
);
print data=data
| mv-expand data
| evaluate bag_unpack(data)
| extend contentLength=tolong(contentLength)
| parse name with * "year=" year:int "/month=" month:int "/day=" day:int "/" *
| extend creationTime=make_datetime(year, month, day)
| extend e = pack("name", name, "contentLength", contentLength)
| summarize blobs=make_list(name) by creationTime
| extend p = pack("creationTime", creationTime, "blobs", blobs)
| summarize make_list(p)
